mapper.select:
select *
from users
where id = #{id}
and password = #{password}

select cv_savedpath
from cv
where cv_userid = #{id}

select name, email, to_char(birthdate, 'yyyy-mm-dd') birthdate, age, address, tel, gender, image
from users
where id = #{id}

mapper.insert:
insert into education
(
	edu_num
	, edu_userid
	, edu_school
	, edu_major
	, edu_degree
	, edu_gpa
	, edu_startdate
	, edu_enddate
)
values
(
	edu_seq.nextval
	, #{edu_userid}
	, #{edu_school}
	, #{edu_major}
	, #{edu_degree}
	, #{edu_gpa}
	, #{edu_startdate}
	, #{edu_enddate}
)

insert into career
(
	career_num
	, career_userid
	, career_title
	, career_org
	, career_dept
	, career_startdate
	, career_enddate
)
values
(
	career_seq.nextval
	, #{career_userid}
	, #{career_title}
	, #{career_org}
	, #{career_dept}
	, #{career_startdate}
	, #{career_enddate}
)

insert into license
(
	license_num
	, license_userid
	, license_title
	, license_org
	, license_ref
	, license_date
)
values
(
	license_seq.nextval
	, #{licc_userid}
	, #{licc_title}
	, #{licc_org}
	, #{licc_ref}
	, #{licc_date}
)

insert into cv
(
	cv_userid
	, cv_savedpath
)
values
(
	#{cv_userid}
	,cv_savedpath
)
